import React, { useEffect, useState } from 'react'
import axios from 'axios'

export default function Movies() {

    const [movies, setMovies]=useState([])

    useEffect(()=>{
        axios
        .get('http://api.themoviedb.org/3/movie/popular?api_key=0635aa13dcf8f77aa3d3a659b24086cc',{
            params:{
                limit:5,
            },
        })
        .then((res)=>setMovies(res.data.results))
        .catch((err)=>console.log(err))
    })


  return (
    <div className='container-fluid bg-dark'>
        <h1>Movies</h1>
        <div className='row'>
            {/* looping over the movies list */}
            {movies.map(movie => {
                return (
                    <div className="card col-3 m" key={movie.id}>
                        <img src={`https://image.tmdb.org/t/p/w500/${movie.backdrop_path}`} className="card-img-top" alt={movie.title}/>
                        <div className="card-body">
                            <h5 className="card-title">{movie.title}</h5>
                            <a href="#" className="btn btn-primary">Details</a>
                        </div>
                    </div>
                )
            })}
        </div>


    </div>
  )
}
