import React, { useEffect, useState } from 'react'
import { useSelector } from 'react-redux'
import { Link } from 'react-router-dom'
import { AxiosInstance } from '../../Network/AxiosInstance'


export default function Movies() {

    const [movies, setMovies]=useState([])

    const spinner = useSelector((state)=> state)

    useEffect(()=>{
        let isMounted = true;
        AxiosInstance
        .get('popular?api_key=0635aa13dcf8f77aa3d3a659b24086cc')
        .then((res)=>setMovies(res.data.results))
        .catch((err)=>console.log(err))
        return () => { isMounted = false }; // cleanup toggles value, if unmounted
    }, []);


  return (
    <div className='container-fluid '>
        <h1 className='p-3'>Movies</h1>

            <div className='row d-flex justify-content-center'>
                {/* looping over the movies list */}
                {movies.map(movie => {
                    return (
                        <div className="card col-3 mx-2 my-2" key={movie.id}>
                            <img src={`https://image.tmdb.org/t/p/w500/${movie.backdrop_path}`} className="card-img-top" alt={movie.title} />
                            <div className="card-body">
                                <h5 className="card-title">{movie.title}</h5>
                                <Link to={`/movie/${movie.id}`} className="btn btn-primary">Details</Link>
                            </div>
                        </div>
                    )
                })}
            </div>

    </div>
  )
}
