import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import { AxiosInstance } from '../../Network/AxiosInstance';

export default function MovieDetails() {

    const [movie, setMovieDetails]=useState({})
    const params = useParams();

    useEffect(()=>{
        let isMounted = true;
        AxiosInstance
        .get(`${params.id}?api_key=0635aa13dcf8f77aa3d3a659b24086cc`)
        .then((res)=>{if (isMounted) setMovieDetails(res.data)})
        .catch((err)=>console.log(err))
        return () => { isMounted = false }; // cleanup toggles value, if unmounted
    }, []);

  return (
    <div className='container'>




        <div class="card mb-3" >
  <div class="row g-0">
    <div class="col-md-4">
      <img src={`https://image.tmdb.org/t/p/w500/${movie.backdrop_path}`} class="img-fluid rounded-start" alt="..."/>
    </div>
    <div class="col-md-8">
      <div class="card-body">
        <h3 class="card-title">{movie.title}</h3>
        <p class="card-text">{movie.overview}</p>
        <p class="card-text"><small class="text-muted"><p>{movie.release_date}</p> </small></p>
      </div>
    </div>
  </div>
</div>
    </div>
  )
}
